import { render } from '@testing-library/react'
import React, { Component } from 'react'
import {Route, Link, Switch, Redirect} from 'react-router-dom'
import './RestaurantDetail.css'
import RestaurantMenu from './RestaurantMenu'
import Cart from './Cart'
import Axios from 'axios'

export default class RestaurantDetail extends Component{
    constructor(){
        super()
        this.state ={
            isClicked: false,
            menu: '',
            cart: []
        }
        this.getList = this.getList.bind(this);
        this.addToCart = this.addToCart.bind(this);
    }
getList(i){
    // console.log(i.target.id)
    this.setState({isClicked:false})
    this.setState({isClicked: true})
    let list = this.props.restaurant.menus[0].menu_sections[i.target.id].menu_items
    // console.log(list)
    this.setState({menu: list})
}
addToCart(i){
    let item = this.state.menu[i.target.id]
    let cart = this.state.cart
    let newCart = cart.push(item)
    // console.log(cart)
    this.setState({cart: cart})


}
addRestaurant = async (e)=>{
    e.preventDefault();
    let response = await Axios.get(
        'http://localhost:3001/api/restaurants/'
    )
    console.log(response)
}
render(){
    // console.log(this.props.restaurant.menus)
    let menus = this.props.restaurant.menus[0].menu_sections
    // console.log(menus)
    const menuList = menus.map((menu, index)=>{
    return(

            <a onClick = {this.getList} id ={index}>{menu.section_name}</a>

    )
    })
    // if(this.state.isClicked == true){
    // const menu = this.state.menu.map((item)=>{
    //     return(
    //         <a>{item}</a>
    //     )
    // })
    // }else{

    //     return <a>{this.state.menu}</a>
    // }
    return(
        <div>
        <div className='menulist'>
            {menuList}
        </div>
        <div className='menu'>
            <RestaurantMenu isClicked={this.state.isClicked} menu = {this.state.menu} addToCart={this.addToCart}/>
        </div>
        <div className='cart'>
            <h1>CART</h1>
            <Cart cart={this.state.cart}/>
        </div>
        </div>



    )

}
}
