import { render } from '@testing-library/react'
import React, { Component } from 'react'
import {Route, Link, Switch, Redirect} from 'react-router-dom'
import './RestaurantList.css'

export default class RestaurantList extends Component{
    constructor(){
        super()
        this.state = {
            isclicked : false,
        }
    }

render(){
    const restaurantList = this.props.restaurantlist.map((restaurant)=>{
        return(
        <div className='list'>

            <h1 className='restaurant'>{restaurant.name}</h1>
            <p className='address'>{restaurant.vicinity}</p>
            <button><span>ORDER</span>
            </button>

        </div>
        )
    })
    // when button is clicked, saves state to be restaurant of whats clicked
    // and isclicked in state is true. Do conditional rendering then so that half the page is 
    // the restaurant menu component with the props sent down
    if(this.state.isclicked == false){
    return(
        <div>
            <h1>Your Local Restaurants:</h1>
            <div className='reslist'>
                {restaurantList}
            </div>
        </div>
    )
    }else{
        <div>
            <h1>Your Local Restaurants:</h1>
            <div className='reslist'>
                {restaurantList}
            </div>
            <div>
                <h1>DETAILS</h1>
            </div>
        </div>
    }
}
}
