import { render } from '@testing-library/react'
import React, { Component } from 'react'
import {Route, Link, Switch, Redirect} from 'react-router-dom'
import './RestaurantList.css'

export default class RestaurantList extends Component{
    constructor(){
        super()
        this.state = {
            isclicked : false,
            restaurant: ''
        }
        this.showDetails = this.showDetails.bind(this);
    }
showDetails(i){
    this.setState({isclicked: true})
    console.log(i)
    // this.setState({restaurant: restaurant.name})
}
render(){
    const restaurantList = this.props.restaurantlist.map((restaurant)=>{
        return(
        <div className='list'>

            <h1 className='restaurant'>{restaurant.name}</h1>
            <p className='address'>{restaurant.vicinity}</p>
            <button value={restaurant.name} onClick={(e)=> this.showDetails(e,'value')}><span>ORDER</span>
            </button>

        </div>
        )
    })

    if(this.state.isclicked == false){
    return(
        <div>
            <h1>Your Local Restaurants:</h1>
            <div className='reslist'>
                {restaurantList}
            </div>
        </div>
    )
    }else{
        return(
        <div className='detailview'>
            <h1>Your Local Restaurants:</h1>
            <div className='reslist1'>
                {restaurantList}
            </div>
            <div className='resdetails'>
                <h1>DETAILS</h1>
            </div>
        </div>
        )
    }
}
}
